package com.networknt.light.rule;

import com.fasterxml.jackson.core.type.TypeReference;
import com.fasterxml.jackson.databind.ObjectMapper;
import com.googlecode.concurrentlinkedhashmap.ConcurrentLinkedHashMap;
import com.networknt.light.server.DbService;
import com.networknt.light.util.ServiceLocator;
import com.orientechnologies.orient.core.db.document.ODatabaseDocumentTx;
import com.orientechnologies.orient.core.db.record.OIdentifiable;
import com.orientechnologies.orient.core.index.OCompositeKey;
import com.orientechnologies.orient.core.index.OIndex;
import com.orientechnologies.orient.core.metadata.schema.OSchema;
import com.orientechnologies.orient.core.record.impl.ODocument;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

import java.util.Date;
import java.util.HashMap;
import java.util.Map;
import java.util.concurrent.ConcurrentMap;

/**
 * Created by husteve on 10/14/2014.
 */
public abstract class AbstractRule implements Rule {
    static final Logger logger = LoggerFactory.getLogger(AbstractRule.class);

    protected ObjectMapper mapper = ServiceLocator.getInstance().getMapper();
    public abstract boolean execute (Object ...objects) throws Exception;

    protected ODocument getCategoryByRid(String categoryRid) {
        Map<String, Object> categoryMap = (Map<String, Object>)ServiceLocator.getInstance().getMemoryImage("categoryMap");
        ConcurrentMap<Object, Object> cache = (ConcurrentMap<Object, Object>)categoryMap.get("cache");
        if(cache == null) {
            cache = new ConcurrentLinkedHashMap.Builder<Object, Object>()
                    .maximumWeightedCapacity(1000)
                    .build();
            categoryMap.put("cache", cache);
        }
        ODocument category = (ODocument)cache.get("categoryRid");
        if(category == null) {
            // TODO warning to increase cache if this happens.
            category = DbService.getODocumentByRid(categoryRid);
            // put it into the category cache.
            if(category != null) {
                cache.put(categoryRid, category);
            }
        }
        return category;
    }

    protected ODocument getProductByRid(String productRid) {
        Map<String, Object> productMap = (Map<String, Object>)ServiceLocator.getInstance().getMemoryImage("productMap");
        ConcurrentMap<Object, Object> cache = (ConcurrentMap<Object, Object>)productMap.get("cache");
        if(cache == null) {
            cache = new ConcurrentLinkedHashMap.Builder<Object, Object>()
                    .maximumWeightedCapacity(1000)
                    .build();
            productMap.put("cache", cache);
        }
        ODocument product = (ODocument)cache.get("productRid");
        if(product == null) {
            // TODO warning to increase cache if this happens.
            product = DbService.getODocumentByRid(productRid);
            if(product != null) {
                cache.put(productRid, product);
            }
        }
        return product;
    }

    protected Map<String, Object> getEventMap(Map<String, Object> inputMap) {
        Map<String, Object> eventMap = new HashMap<String, Object>();
        Map<String, Object> payload = (Map<String, Object>)inputMap.get("payload");
        if(payload != null) {
            Map<String, Object> user = (Map<String, Object>)payload.get("user");
            if(user != null)  eventMap.put("createUserId", user.get("userId"));
        }
        // IP address is used to identify event owner if user is not logged in.
        if(inputMap.get("ipAddress") != null) {
            eventMap.put("ipAddress", inputMap.get("ipAddress"));
        }
        if(inputMap.get("host") != null) {
            eventMap.put("host", inputMap.get("host"));
        }
        if(inputMap.get("app") != null) {
            eventMap.put("app", inputMap.get("app"));
        }
        eventMap.put("category", inputMap.get("category"));
        eventMap.put("name", inputMap.get("name"));
        eventMap.put("createDate", new java.util.Date());
        eventMap.put("data", new HashMap<String, Object>());
        return eventMap;
    }

    protected ODocument getODocumentByHostId(String index, String host, String id) {
        ODocument doc = null;
        ODatabaseDocumentTx db = ServiceLocator.getInstance().getDb();
        try {
            OIndex<?> hostIdIdx = db.getMetadata().getIndexManager().getIndex(index);
            // this is a unique index, so it retrieves a OIdentifiable
            OCompositeKey key = new OCompositeKey(host, id);
            OIdentifiable oid = (OIdentifiable) hostIdIdx.get(key);
            if (oid != null) {
                doc = (ODocument)oid.getRecord();
            }
        } catch (Exception e) {
            e.printStackTrace();
        } finally {
            db.close();
        }
        return doc;
    }

    public Map<String, Object> getAccessByRuleClass(String ruleClass) throws Exception {
        Map<String, Object> access = null;
        Map<String, Object> accessMap = ServiceLocator.getInstance().getMemoryImage("accessMap");
        ConcurrentMap<Object, Object> cache = (ConcurrentMap<Object, Object>)accessMap.get("cache");
        if(cache == null) {
            cache = new ConcurrentLinkedHashMap.Builder<Object, Object>()
                    .maximumWeightedCapacity(1000)
                    .build();
            accessMap.put("cache", cache);
            logger.error("accessMap cache created =" + cache);
        } else {
            access = (Map<String, Object>)cache.get(ruleClass);
        }
        if(access == null) {
            logger.error("access is null, cache =" + cache);
            ODatabaseDocumentTx db = ServiceLocator.getInstance().getDb();
            try {
                OIndex<?> ruleClassIdx = db.getMetadata().getIndexManager().getIndex("Access.ruleClass");
                // this is a unique index, so it retrieves a OIdentifiable
                OIdentifiable oid = (OIdentifiable) ruleClassIdx.get(ruleClass);
                if (oid != null && oid.getRecord() != null) {
                    String json = oid.getRecord().toJSON();
                    access = mapper.readValue(json,
                            new TypeReference<HashMap<String, Object>>() {
                            });
                    cache.put(ruleClass, access);
                }
            } catch (Exception e) {
                logger.error("Exception:", e);
                throw e;
            } finally {
                db.close();
            }
        }
        return access;
    }

}

package com.networknt.light.rule.access;

import com.fasterxml.jackson.core.type.TypeReference;
import com.fasterxml.jackson.databind.ObjectMapper;
import com.fasterxml.jackson.databind.SerializationFeature;
import com.googlecode.concurrentlinkedhashmap.ConcurrentLinkedHashMap;
import com.networknt.light.rule.AbstractRule;
import com.networknt.light.rule.Rule;
import com.networknt.light.util.ServiceLocator;
import com.orientechnologies.orient.core.db.document.ODatabaseDocumentTx;
import com.orientechnologies.orient.core.db.record.OIdentifiable;
import com.orientechnologies.orient.core.index.OIndex;
import com.orientechnologies.orient.core.metadata.schema.OSchema;
import com.orientechnologies.orient.core.record.impl.ODocument;
import com.orientechnologies.orient.core.serialization.serializer.OJSONWriter;
import com.orientechnologies.orient.core.sql.query.OSQLSynchQuery;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

import java.io.File;
import java.io.IOException;
import java.util.HashMap;
import java.util.List;
import java.util.Map;
import java.util.concurrent.ConcurrentMap;

/**
 * Created by steve on 30/01/15.
 */
public abstract class AbstractAccessRule extends AbstractRule implements Rule {
    static final Logger logger = LoggerFactory.getLogger(AbstractAccessRule.class);

    public abstract boolean execute (Object ...objects) throws Exception;

    protected void updAccess(Map<String, Object> data) throws Exception {
        ODocument access = null;
        ODatabaseDocumentTx db = ServiceLocator.getInstance().getDb();
        try {
            db.begin();
            OIndex<?> ruleClassIdx = db.getMetadata().getIndexManager().getIndex("Access.ruleClass");
            // this is a unique index, so it retrieves a OIdentifiable
            OIdentifiable oid = (OIdentifiable) ruleClassIdx.get(data.get("ruleClass"));
            if (oid != null && oid.getRecord() != null) {
                access = (ODocument) oid.getRecord();
                access.field("accessLevel", data.get("accessLevel"));
                List<String> clients = (List)data.get("clients");
                if(clients != null && clients.size() > 0) {
                    access.field("clients", clients);
                } else {
                    access.removeField("clients");
                }

                List<String> roles = (List)data.get("roles");
                if(roles != null && roles.size() > 0) {
                    access.field("roles", roles);
                } else {
                    access.removeField("roles");
                }

                List<String> users = (List)data.get("users");
                if(users != null && users.size() > 0) {
                    access.field("users", users);
                } else {
                    access.removeField("users");
                }
                access.field("updateDate", data.get("updateDate"));
                access.field("updateUserId", data.get("updateUserId"));
                access.save();
            }
            db.commit();
        } catch (Exception e) {
            db.rollback();
            logger.error("Exception:", e);
            throw e;
        } finally {
            db.close();
        }
        Map<String, Object> accessMap = ServiceLocator.getInstance().getMemoryImage("accessMap");
        ConcurrentMap<Object, Object> cache = (ConcurrentMap<Object, Object>)accessMap.get("cache");
        if(cache == null) {
            cache = new ConcurrentLinkedHashMap.Builder<Object, Object>()
                    .maximumWeightedCapacity(1000)
                    .build();
            accessMap.put("cache", cache);
        }
        String json = access.toJSON();
        cache.put(data.get("ruleClass"), mapper.readValue(json,
                new TypeReference<HashMap<String, Object>>() {
                }));
    }

    protected void delAccess(Map<String, Object> data) throws Exception {
        ODatabaseDocumentTx db = ServiceLocator.getInstance().getDb();
        try {
            db.begin();
            OIndex<?> ruleClassIdx = db.getMetadata().getIndexManager().getIndex("Access.ruleClass");
            // this is a unique index, so it retrieves a OIdentifiable
            OIdentifiable oid = (OIdentifiable) ruleClassIdx.get(data.get("ruleClass"));
            if (oid != null && oid.getRecord() != null) {
                ODocument access = (ODocument) oid.getRecord();
                access.delete();
            }
            db.commit();
        } catch (Exception e) {
            db.rollback();
            logger.error("Exception:", e);
            throw e;
        } finally {
            db.close();
        }
        Map<String, Object> accessMap = ServiceLocator.getInstance().getMemoryImage("accessMap");
        ConcurrentMap<Object, Object> cache = (ConcurrentMap<Object, Object>)accessMap.get("cache");
        if(cache != null) {
            cache.remove(data.get("ruleClass"));
        }
    }

    protected String getAccesses(String host) {
        String sql = "SELECT FROM Access";
        if(host != null) {
            sql = sql + " WHERE host = '" + host;
        }
        String json = null;
        ODatabaseDocumentTx db = ServiceLocator.getInstance().getDb();
        try {
            OSQLSynchQuery<ODocument> query = new OSQLSynchQuery<>(sql);
            List<ODocument> accesses = db.command(query).execute();
            json = OJSONWriter.listToJSON(accesses, null);
        } catch (Exception e) {
            logger.error("Exception:", e);
            throw e;
        } finally {
            db.close();
        }
        return json;
    }

}

package com.networknt.light.rule.access;

import com.networknt.light.rule.Rule;
import com.networknt.light.rule.rule.AbstractRuleRule;

import java.util.Map;

/**
 * Created by steve on 30/01/15.
 */
public class DelAccessEvRule extends AbstractAccessRule implements Rule {
    public boolean execute (Object ...objects) throws Exception {
        Map<String, Object> eventMap = (Map<String, Object>) objects[0];
        Map<String, Object> data = (Map<String, Object>) eventMap.get("data");
        delAccess(data);
        return true;
    }
}

package com.networknt.light.rule.access;

import com.networknt.light.rule.Rule;
import com.networknt.light.rule.RuleEngine;
import com.networknt.light.rule.rule.AbstractRuleRule;
import com.networknt.light.server.DbService;
import com.orientechnologies.orient.core.record.impl.ODocument;

import java.util.List;
import java.util.Map;

/**
 * Created by steve on 30/01/15.
 */
public class DelAccessRule extends AbstractAccessRule implements Rule {
    public boolean execute (Object ...objects) throws Exception {
        Map<String, Object> inputMap = (Map<String, Object>) objects[0];
        Map<String, Object> data = (Map<String, Object>) inputMap.get("data");
        Map<String, Object> payload = (Map<String, Object>) inputMap.get("payload");
        String rid = (String)data.get("@rid");
        int inputVersion = (int)data.get("@version");
        String ruleClass = (String)data.get("ruleClass");
        String error = null;
        if(payload == null) {
            error = "Login is required";
            inputMap.put("responseCode", 401);
        } else {
            Map<String, Object> user = (Map<String, Object>)payload.get("user");
            List roles = (List)user.get("roles");
            if(!roles.contains("owner") && !roles.contains("admin") && !roles.contains("ruleAdmin")) {
                error = "Role owner or admin or ruleAdmin is required to delete rule";
                inputMap.put("responseCode", 403);
            } else {
                String host = (String)user.get("host");
                if(host != null) {
                    if(!host.equals(data.get("host"))) {
                        error = "User can only delete access control from host: " + host;
                        inputMap.put("responseCode", 403);
                    } else {
                        // check if the ruleClass contains the host.
                        if(host != null && !ruleClass.contains(host)) {
                            // you are not allowed to delete access control to the rule as it is not belong to the host.
                            error = "ruleClass is not owned by the host: " + host;
                            inputMap.put("responseCode", 403);
                        } else {
                            // check if the access control exist or not.
                            ODocument access = DbService.getODocumentByRid(rid);
                            if(access == null) {
                                error = "Access control with @rid " + rid + " cannot be found";
                                inputMap.put("responseCode", 404);
                            } else {
                                int storedVersion = access.field("@version");
                                if(inputVersion != storedVersion) {
                                    error = "Deleting version " + inputVersion + " doesn't match stored version " + storedVersion;
                                    inputMap.put("responseCode", 400);
                                } else {
                                    Map eventMap = getEventMap(inputMap);
                                    Map<String, Object> eventData = (Map<String, Object>)eventMap.get("data");
                                    inputMap.put("eventMap", eventMap);
                                    eventData.put("ruleClass", ruleClass);
                                    eventData.put("updateDate", new java.util.Date());
                                    eventData.put("updateUserId", user.get("userId"));
                                }
                            }
                        }
                    }
                } else {
                    // check if the access control exist or not.
                    ODocument access = DbService.getODocumentByRid(rid);
                    if(access == null) {
                        error = "Access control with @rid " + rid + " cannot be found";
                        inputMap.put("responseCode", 404);
                    } else {
                        int storedVersion = access.field("@version");
                        if(inputVersion != storedVersion) {
                            error = "Deleting version " + inputVersion + " doesn't match stored version " + storedVersion;
                            inputMap.put("responseCode", 400);
                        } else {
                            Map eventMap = getEventMap(inputMap);
                            Map<String, Object> eventData = (Map<String, Object>)eventMap.get("data");
                            inputMap.put("eventMap", eventMap);
                            eventData.put("ruleClass", ruleClass);
                            eventData.put("updateDate", new java.util.Date());
                            eventData.put("updateUserId", user.get("userId"));
                        }
                    }
                }
            }
        }
        if(error != null) {
            inputMap.put("error", error);
            return false;
        } else {
            return true;
        }
    }
}

package com.networknt.light.rule.access;

import com.networknt.light.rule.Rule;
import com.networknt.light.rule.rule.AbstractRuleRule;
import com.orientechnologies.orient.core.record.impl.ODocument;
import org.slf4j.LoggerFactory;

import java.util.List;
import java.util.Map;

/**
 * Created by steve on 30/01/15.
 */
public class GetAccessRule extends AbstractAccessRule implements Rule {
    static final org.slf4j.Logger logger = LoggerFactory.getLogger(GetAccessRule.class);
    public boolean execute (Object ...objects) throws Exception {
        Map<String, Object> inputMap = (Map<String, Object>)objects[0];
        Map<String, Object> data = (Map<String, Object>)inputMap.get("data");
        String ruleClass = (String)data.get("ruleClass");
        Map<String, Object> access = getAccessByRuleClass(ruleClass);
        if(access != null) {
            inputMap.put("result", mapper.writeValueAsString(access));
            return true;
        } else {
            inputMap.put("result", "No access control can be found for ruleClass" + ruleClass);
            inputMap.put("responseCode", 404);
            return false;
        }
    }
}

package com.networknt.light.rule.access;

import com.networknt.light.rule.Rule;

import java.util.List;
import java.util.Map;

/**
 * Created by steve on 01/02/15.
 */
public class GetAllAccessRule extends AbstractAccessRule implements Rule {
    public boolean execute (Object ...objects) throws Exception {
        Map<String, Object> inputMap = (Map<String, Object>) objects[0];
        Map<String, Object> payload = (Map<String, Object>) inputMap.get("payload");
        Map<String, Object> user = (Map<String, Object>) payload.get("user");
        List roles = (List)user.get("roles");
        if(roles.contains("owner") || roles.contains("admin") || roles.contains("ruleAdmin")) {
            String host = (String) user.get("host");
            String hostAccesses = getAccesses(host);
            if(hostAccesses != null) {
                inputMap.put("result", hostAccesses);
                return true;
            } else {
                inputMap.put("result", "No access control can be found.");
                inputMap.put("responseCode", 404);
                return false;
            }
        } else {
            inputMap.put("result", "Permission denied");
            inputMap.put("responseCode", 403);
            return false;
        }
    }
}

package com.networknt.light.rule.access;

import com.networknt.light.rule.Rule;
import com.networknt.light.rule.rule.AbstractRuleRule;

import java.util.Map;

/**
 * Created by steve on 30/01/15.
 */
public class UpdAccessEvRule extends AbstractAccessRule implements Rule {
