package com.networknt.light.rule.form;

import com.fasterxml.jackson.core.type.TypeReference;
import com.networknt.light.rule.Rule;

import java.util.HashMap;
import java.util.List;
import java.util.Map;

/**
 * Created by steve on 8/29/2014.
 *
 */
public class UpdFormRule extends AbstractFormRule implements Rule {
    public boolean execute (Object ...objects) throws Exception {
        Map<String, Object> inputMap = (Map<String, Object>)objects[0];
        Map<String, Object> data = (Map<String, Object>)inputMap.get("data");
        Map<String, Object> payload = (Map<String, Object>) inputMap.get("payload");
        String host = (String)data.get("host");
        String error = null;
        if(payload == null) {
            error = "Login is required";
            inputMap.put("responseCode", 401);
        } else {
            Map<String, Object> user = (Map<String, Object>)payload.get("user");
            List roles = (List)user.get("roles");
            if(!roles.contains("owner") && !roles.contains("admin") && !roles.contains("formAdmin")) {
                error = "Role owner or admin or formAdmin is required to update form";
                inputMap.put("responseCode", 401);
            } else {
                String userHost = (String)user.get("host");
                if(userHost != null && !userHost.equals(host)) {
                    error = "User can only update form for host: " + host;
                    inputMap.put("responseCode", 401);
                } else {
                    int inputVersion = (int)data.get("@version");
                    String id = (String)data.get("id");
                    String json = getFormById(inputMap);
                    if(json == null) {
                        error = "Form with id " + id + " cannot be found";
                        inputMap.put("responseCode", 404);
                    } else {
                        Map<String, Object> form = mapper.readValue(json,
                                new TypeReference<HashMap<String, Object>>() {
                                });
                        int storedVersion = (int)form.get("@version");
                        if(inputVersion != storedVersion) {
                            inputMap.put("responseCode", 400);
                            error = "Updating version " + inputVersion + " doesn't match stored version " + storedVersion;
                        } else {
                            Map eventMap = getEventMap(inputMap);
                            Map<String, Object> eventData = (Map<String, Object>)eventMap.get("data");
                            inputMap.put("eventMap", eventMap);
                            eventData.putAll((Map<String, Object>)inputMap.get("data"));
                            eventData.put("updateDate", new java.util.Date());
                            eventData.put("updateUserId", user.get("userId"));
                            // no need to remove host from eventData as owner update won't touch id and host.
                        }
                    }
                }
            }
        }
        if(error != null) {
            inputMap.put("error", error);
            return false;
        } else {
            return true;
        }
    }
}

package com.networknt.light.rule.host;

import com.fasterxml.jackson.databind.ObjectMapper;
import com.fasterxml.jackson.databind.SerializationFeature;
import com.networknt.light.rule.AbstractRule;
import com.networknt.light.rule.Rule;
import com.networknt.light.util.ServiceLocator;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

import java.io.File;
import java.io.IOException;
import java.util.HashMap;
import java.util.Map;

/**
 * Created by Steve Hu on 2015-01-19.
 */
public abstract class AbstractHostRule extends AbstractRule implements Rule {
    static final Logger logger = LoggerFactory.getLogger(ServiceLocator.class);
    ObjectMapper mapper = ServiceLocator.getInstance().getMapper();

    public abstract boolean execute (Object ...objects) throws Exception;

    protected void addHost(Map<String, Object> data) throws Exception {
        Map<String, Object> hostMap = ServiceLocator.getInstance().getHostMap();
        Map<String, Object> map = new HashMap<String, Object>();
        map.put("base", data.get("base"));
        map.put("transferMinSize", data.get("transferMinSize"));
        hostMap.put((String)data.get("id"), map);
        writeHostMap(hostMap);
    }

    protected void updHost(Map<String, Object> data) throws Exception {
        Map<String, Object> hostMap = ServiceLocator.getInstance().getHostMap();
        Map<String, Object> map = (Map<String, Object>)hostMap.get(data.get("id"));
        map.put("base", data.get("base"));
        map.put("transferMinSize", data.get("transferMinSize"));
        writeHostMap(hostMap);
    }

    protected void delHost(Map<String, Object> data) throws Exception {
        Map<String, Object> hostMap = ServiceLocator.getInstance().getHostMap();
        hostMap.remove(data.get("id"));
        writeHostMap(hostMap);
    }

    private void writeHostMap(Map<String, Object> hostMap) {
        try {
            mapper.enable(SerializationFeature.INDENT_OUTPUT);
            mapper.writeValue((new File(System.getProperty("user.home") + "/virtualhost.json")), hostMap);
        } catch (IOException ioe) {
            logger.error("Exception:", ioe);
        }
    }
}

