    protected void revokeRefreshToken(Map<String, Object> data) throws Exception {
        ODatabaseDocumentTx db = ServiceLocator.getInstance().getDb();
        try {
            db.begin();
            OIndex<?> userIdIdx = db.getMetadata().getIndexManager().getIndex("User.userId");
